(function() {
  'use strict';
  var pressed = false;

  $(document).keydown(function(e){
    if (e.keyCode == 32 && !pressed) {
        $('video').last().add('.spacebar').stop().fadeOut();
        pressed = true;
        console.log(e.keyCode);
    }
  });
  $(document).keyup(function(e){
  // Do we already know it's down?
    console.log(e.keyCode);
    if (e.keyCode == 32) {
        $('video').last().add('.spacebar').stop().fadeIn();
        pressed = false;
    }
  });

}());
